SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select distinct task.task_id as taskId, task.cus_id as cusId, task.task_type as taskType, task.task_create_date as taskCreateDate, task.task_request_time as taskRequestTime, task.qnt as qnt, task.loan_totl_amt as LoanTotlAmt, task.loan_balance as loanBalance, task.task_huser as taskHuser, task.task_horg as taskHorg, task.manager_id as managerId, task.manager_br_id as managerBrId, task.check_time as checkTime, task.check_addr as checkAddr, task.agreed_person as agreedPeson, task.remarks as remarks, task.approve_status as approveStatus, task.industry_outlook as industryOutlook, task.repayment as repayment, task.reciprocal_type as reciprocalType, task.contact_information as contactInformation, ecif.client_name as chineseName, ecif.global_id as cardId, task.REPAYMENT_OTHER as repaymentOther, task.CHECK_DESCRIBE as checkDescribe, task.FALLBACK_REASON as fallbackReason from psp_check_task task left join qz_iesb_for_ecif ecif on task.cus_id = ecif.client_no where 1 = 1 and ecif.CLIENT_NO is not null AND ecif.CUST_MANAGER_ID in ( select T2.EXTERNAL_ID from ACCOUNT_MANAGER_PARAMETER t1 left join SYS_USER t2 on t1.USER_ID = T2.id where T1.id in (SELECT mbm.child_id FROM manager_belong_map mbm LEFT JOIN account_manager_parameter amp ON amp. ID = mbm.parent_id WHERE amp.user_id = #{userId}) or T1.id = #{userId})
AND ecif.client_name=#{chineseName}
AND ecif.global_id=#{cardId}
AND ecif.client_no=#{clientNo}
and ( task.approve_status='1' or (task.approve_status='0' and task.FALLBACK_REASON is not null) )
and task.approve_status='1'
and task.approve_status='0' and task.FALLBACK_REASON is not null
AND (task.task_type=#{checkType}
or task.CHECK_DESCRIBE like '%首次放款检查%'
or task.CHECK_DESCRIBE like '%满额用信%'
or task.CHECK_DESCRIBE like '%授信到期前最高额用信检查%'
or task.CHECK_DESCRIBE like '%放款次月客户回访%'
or task.CHECK_DESCRIBE like '%每月电话回访%'
or task.CHECK_DESCRIBE like '%季度回访%'
)
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

