with circle as (select CIRCLE.*,CAI.PRODUCT_ID,bci.user_id as manager_id,pa.product_name from CUSTOMER_APPLICATION_INFO cai left join QZ_IESB_FOR_CIRCLE circle on CAI.id = CIRCLE.APPLICATION_ID left join BASIC_CUSTOMER_INFORMATION bci on cai.customer_id = bci.id left join product_attribute pa on CAI.PRODUCT_ID = pa.id where CAI.STATUS = 'approved')
SELECT * FROM (SELECT ROW_.*,ROWNUM row_index FROM ( select distinct su.display_name as managerId, sd.name as orgName, loan.bill_no as billNo, acct.acct_no as acctNo, acct.acct_name as acctName, loan.REALITY_IR_Y as realityIrY, circle.START_DATE as contStartDate, circle.END_DATE as contEndDate, cont.CONT_AMT as contAmt, loan.LOAN_AMT as loanAmt, loan.LOAN_BALANCE as loanBalance, (loan.Rec_Int_Accum - loan.recv_int_accum) as intAccum, loan.DISTR_DATE as qixiDate, loan.ORI_END_DATE as distrDate, loan.ACC_STATUS as accStatus, circle.PRODUCT_ID as productId, circle.product_name as productName, circle.client_name as clientName from O_CLPM_ACC_LOAN loan inner join circle on loan.cont_no = circle.RET_CONTNO inner join O_CLPM_IQP_CUS_ACCT acct on loan.cont_no = acct.cont_no inner join O_CLPM_CTR_LOAN_CONT cont on loan.cont_no = cont.cont_no inner join sys_user su on circle.user_id = su.id inner join SYS_ORGANIZATION sd on circle.regist_org_no = sd.id where 1 = 1 and acct.acct_attr = '03' and (loan.prd_id='100084' or loan.prd_id='100085') and to_date(loan.distr_date,'yyyy-mm-dd') between #{startDate} and #{endDate}
and circle.user_id = #{userId}
and circle.regist_org_no = #{orgId}
and circle.client_name = #{clientName}
and su.id = #{userId}
and circle.PRODUCT_ID = #{productId}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE row_index > #{limit}*#{page}

