with circle as (select CIRCLE.*,CAI.PRODUCT_ID,bci.user_id as manager_id from CUSTOMER_APPLICATION_INFO cai left join QZ_IESB_FOR_CIRCLE circle on CAI.id = CIRCLE.APPLICATION_ID left join BASIC_CUSTOMER_INFORMATION bci on cai.customer_id = bci.id where CAI.STATUS = 'approved')
SELECT * FROM (SELECT ROW_.*,ROWNUM row_index FROM (
select to_char(#{endDate},'yyyy-MM-dd') as pstime,sum(PS_NORM_INT_AMT) as PS_NORM_INT_AMT,sum(PS_OD_INT_AMT) as PS_OD_INT_AMT, sum(SETL_NORM_INT) as SETL_NORM_INT,sum(SETL_OD_INT_AMT) as SETL_OD_INT_AMT, to_number(sum(PS_NORM_INT_AMT))+to_number(sum(PS_OD_INT_AMT))-to_number(sum(SETL_NORM_INT))-to_number(sum(SETL_OD_INT_AMT)) as ps_amt, loan.CUS_ID as cus_id,circle.client_name as client_name from O_CLPM_ACC_LOAN loan inner join O_CCH_LM_PM_SHD shd on loan.BILL_NO = shd.LOAN_NO inner join circle on loan.CUS_ID = circle.client_no where loan.prd_id in ('100084','100085') and loan.ACC_STATUS = '1' and shd.SETL_IND = 'N' and to_date(shd.PS_DUE_DT,'yyyy-mm-dd') <= #{endDate} and circle.manager_id = #{userId} group by loan.CUS_ID,circle.client_name
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE row_index > #{limit}*#{page}
