  FROM （select count(cont.cont_no) as newUsedLoans
  from o_clpm_ctr_loan_cont cont
 inner join (select loan.cont_no
               from o_clpm_acc_loan loan
               left join qz_iesb_for_ecif ecif on loan.cus_id =
                                                  ecif.client_no
               left join customer_application_info info on ecif.customer_id =
                                                           info.customer_id
              where loan.distr_date  >=   #{startDate}
                and loan.distr_date  <=   #{endDate}
                
                and info.product_id = #{productId}
                
                
                	and ecif.customer_id =#{userId}
                            
              order by loan.cont_no) loans on cont.cont_no = loans.cont_no
 where cont.cont_start_date  <=   #{endDate}
   and cont.cont_start_date  >=   #{startDate} ） t1,
 (select count(cont.cont_no) as allLoans
          from o_clpm_ctr_loan_cont cont
         inner join (select loan.cont_no
                      from o_clpm_acc_loan loan
                      left join qz_iesb_for_ecif ecif on loan.cus_id =
                                                         ecif.client_no
                      left join customer_application_info info on ecif.customer_id =
                                                                  info.customer_id
                     where loan.distr_date  <=   #{endDate}
                       
                		and info.product_id = #{productId}
                 	   
                 	   
                		and ecif.customer_id =#{userId}
                	    
                     order by loan.cont_no) loans on cont.cont_no =
                                                     loans.cont_no
         where cont.cont_start_date  <=   #{endDate}) t2,
 (select count(cont.cont_no) as newCreditLoans
