                			and info.product_id = #{productId}
                 			
                 			
                				and ecif.customer_id =#{userId}
            				) t,
                       (select sum(cont.cont_balance) as contBalace
                          from o_clpm_ctr_loan_cont cont
                          left join qz_iesb_for_ecif ecif on cont.cus_id =
                                                             ecif.client_no
