                   and his.data_dt  >=   #{startDate}
                    
                		and info.product_id = #{productId}
                    
                    
                		and ecif.customer_id =#{userId}
            		
                 group by data_dt)) t8,
 (select sum(loan.overdue_balance) as overdueBalance
